// @ts-check
import { AbstractHandler } from './AbstractHandler.js'

/**
 * @typedef {import('../../utils/Command.js').Command} Command
 */
class ValueHandler extends AbstractHandler {
    constructor() {
        super()
    }

    /**
     * @param {Command} command
     * @returns {unknown}
     */
    process(command) {
        const { payload } = command
        return payload[0]
    }
}

export { ValueHandler }
