// @ts-check
/**
 * TypesHandler class provides utilities for converting between types.
 */
class TypesHandler {
    /**
     * Converts a JavaScript type to a JType equivalent.
     * @param {Function} type - The JavaScript type.
     * @returns {string} The corresponding type name
     */
    static convertTypeToJType(type) {
        switch (type) {
            case Boolean:
                return 'JType.Boolean'
            case Number:
                return 'JType.Float'
            case String:
                return 'JType.String'
            case Object:
                return typeof type
            default:
                return typeof type
        }
    }

    /**
     * @param {unknown} value
     * @returns {boolean}
     */
    static isPrimitiveOrNullOrUndefined(value) {
        return (
            value === null ||
            value === undefined ||
            typeof value === 'string' ||
            typeof value === 'number' ||
            typeof value === 'boolean'
        )
    }
}

export { TypesHandler }
